// App.js

import React, { useState } from 'react';
import ChatbotLogo from './ChatbotLogo.png';  // Asegúrate de que la ruta sea correcta

function App() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Aquí es donde integrarías Firebase para manejar el inicio de sesión
    console.log('Email:', email);
    console.log('Password:', password);
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', flexDirection: 'column' }}>
      <img src={ChatbotLogo} alt="Chatbot Logo" style={{ marginBottom: '20px' }} />
      <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', width: '300px' }}>
        <h2>Inicio de Sesión</h2>
        <label>
          Correo:
          <input type="email" value={email} onChange={handleEmailChange} required />
        </label>
        <label>
          Contraseña:
          <input type="password" value={password} onChange={handlePasswordChange} required />
        </label>
        <button type="submit">Iniciar Sesión</button>
      </form>
    </div>
  );
}

export default App;
