import React, { useState } from 'react';
import './App.css';
import ChatbotLogo from './Images/ChatBotLogo.png';
import OracleLogo from './Images/OracleLogo.png';
import { signInWithEmailAndPassword } from 'firebase/auth';
import { auth, firestore } from './Database Connection/firebase'; // Importa auth y firestore
import { addDoc, collection, getDocs, query, where } from 'firebase/firestore'; // Importa las funciones necesarias de Firestore
import HomeScreen from './Screens/homeScreen';

async function createUserIfNotExists(email) {
  const q = query(collection(firestore, 'usuarios'), where('correo', '==', email));
  const querySnapshot = await getDocs(q);
  if (querySnapshot.size === 0) {
    // El documento no existe, entonces crea uno nuevo
    await addDoc(collection(firestore, 'usuarios'), { correo: email });
  }
}

function App() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loggedIn, setLoggedIn] = useState(false);

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const userCredential = await signInWithEmailAndPassword(auth, email, password);
      const user = userCredential.user;

      // Verificar si el usuario ya existe en Firestore, si no, crearlo
      await createUserIfNotExists(user.email);

      setLoggedIn(true);
    } catch (error) {
      setError('Correo o contraseña incorrectos');
      console.error('Error al iniciar sesión:', error.message);
    }
  };

  if (loggedIn) {
    return <HomeScreen />;
  }

  return (
    <div className="App">
      <header className="App-header">
        <img src={ChatbotLogo} className="App-logo" alt="Chatbot Logo" />
        <form onSubmit={handleSubmit} className="App-form">
          <h2>Inicio de Sesión</h2>
          {error && <p style={{ color: 'red' }}>{error}</p>}
          <label>
            Correo:
            <input type="email" value={email} onChange={handleEmailChange} required />
          </label>
          <label>
            Contraseña:
            <input type="password" value={password} onChange={handlePasswordChange} required />
          </label>
          <button type="submit">Iniciar Sesión</button>
        </form>
        <img src={OracleLogo} className="Oracle-logo" alt="Oracle Logo" />
      </header>
    </div>
  );
}

export default App;
