import React, { useState, useEffect } from 'react';
import './App.css';
import ChatbotLogo from './Images/ChatBotLogo.png';
import OracleLogo from './Images/OracleLogo.png';
import { signInWithEmailAndPassword } from 'firebase/auth';
import { auth, firestore } from './Database Connection/firebase'; // Importa auth y firestore
import { collection, getDocs, query, where, addDoc, doc, setDoc } from 'firebase/firestore'; // Importa Firestore funciones
import HomeScreen from './Screens/homeScreen';

function App() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loggedIn, setLoggedIn] = useState(false);

  useEffect(() => {
    const checkUser = async () => {
      try {
        // Consulta Firestore para verificar si el usuario ya existe
        const q = query(collection(firestore, 'usuarios'), where('correo', '==', email));
        const querySnapshot = await getDocs(q);
        if (querySnapshot.size > 0) {
          // Si el usuario ya existe, establece loggedIn a true
          setLoggedIn(true);
        }
      } catch (error) {
        console.error('Error al verificar usuario en Firestore:', error.message);
      }
    };

    if (loggedIn) {
      // Si loggedIn es true, redirige a HomeScreen
      return <HomeScreen />;
    } else {
      // Si loggedIn es false, verifica si el usuario ya existe
      checkUser();
    }
  }, [email, loggedIn, firestore]);

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const userCredential = await signInWithEmailAndPassword(auth, email, password);
      const user = userCredential.user;

      // Verificar nuevamente si el usuario ya existe en Firestore
      const q = query(collection(firestore, 'usuarios'), where('correo', '==', email));
      const querySnapshot = await getDocs(q);
      if (querySnapshot.size === 0) {
        // Si el usuario no existe, crear un nuevo documento en la colección "usuarios" en Firestore
        await createUserDocument(user.email);
      }

      setLoggedIn(true);
    } catch (error) {
      setError('Correo o contraseña incorrectos');
      console.error('Error al iniciar sesión:', error.message);
    }
  };

  const createUserDocument = async (email) => {
    try {
      // Generar un ID único para el nuevo documento de usuario en Firestore
      const newUserRef = doc(collection(firestore, 'usuarios'));
      // Crear un nuevo documento de usuario con el correo electrónico del usuario
      await setDoc(newUserRef, { correo: email });
    } catch (error) {
      console.error('Error al crear documento de usuario en Firestore:', error.message);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <img src={ChatbotLogo} className="App-logo" alt="Chatbot Logo" />
        <form onSubmit={handleSubmit} className="App-form">
          <h2>Inicio de Sesión</h2>
          {error && <p style={{ color: 'red' }}>{error}</p>}
          <label>
            Correo:
            <input type="email" value={email} onChange={handleEmailChange} required />
          </label>
          <label>
            Contraseña:
            <input type="password" value={password} onChange={handlePasswordChange} required />
          </label>
          <button type="submit">Iniciar Sesión</button>
        </form>
        <img src={OracleLogo} className="Oracle-logo" alt="Oracle Logo" />
      </header>
    </div>
  );
}

export default App;
