import React, { useState } from 'react';
import { useHistory } from 'react-router-dom'; // Importa useHistory para redirigir a otra página
import './App.css';
import ChatbotLogo from './Images/ChatBotLogo.png';
import OracleLogo from './Images/OracleLogo.png';
import { signInWithEmailAndPassword } from 'firebase/auth';
import { auth } from './firebase'; // Asegúrate de tener correctamente configurado tu archivo firebase.js

function App() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const history = useHistory();

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Verificar las credenciales utilizando Firebase Authentication
      await signInWithEmailAndPassword(auth, email, password);

      // Si las credenciales son correctas, redirigir al usuario a otra página
      history.push('/home');
    } catch (error) {
      // Si ocurre un error, mostrar un mensaje de error al usuario
      setErrorMessage('Correo o contraseña incorrectos');
      console.error('Error al iniciar sesión:', error.message);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <img src={ChatbotLogo} className="App-logo" alt="Chatbot Logo" />
        <form onSubmit={handleSubmit} className="App-form">
          <h2>Inicio de Sesión</h2>
          <label>
            Correo:
            <input type="email" value={email} onChange={handleEmailChange} required />
          </label>
          <label>
            Contraseña:
            <input type="password" value={password} onChange={handlePasswordChange} required />
          </label>
          <button type="submit">Iniciar Sesión</button>
          {errorMessage && <p className="error-message">{errorMessage}</p>}
        </form>
      </header>
      <footer className="App-footer">
        <img src={OracleLogo} className="Footer-logo" alt="Oracle Logo" />
      </footer>
    </div>
  );
}

export default App;
