import React, { useState } from 'react';
import './App.css';
import { BrowserRouter as Router, Route, Redirect } from 'react-router-dom';
import ChatbotLogo from './Images/ChatBotLogo.png';
import OracleLogo from './Images/OracleLogo.png';
import { signInWithEmailAndPassword } from 'firebase/auth';
import { auth } from './firebase';

function App() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loggedIn, setLoggedIn] = useState(false);
  const [error, setError] = useState('');

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Verificar las credenciales utilizando Firebase Authentication
      await signInWithEmailAndPassword(auth, email, password);

      // Si las credenciales son correctas, establecer loggedIn en true
      setLoggedIn(true);
    } catch (error) {
      // Si ocurre un error, mostrar un mensaje de error al usuario y limpiar las credenciales después de unos segundos
      setError('Correo o contraseña incorrectos');
      setTimeout(() => {
        setError('');
      }, 3000);
    }
  };

  return (
    <Router>
      <div className="App">
        <Route exact path="/">
          {loggedIn ? <Redirect to="/success" /> : null}
          <header className="App-header">
            <img src={ChatbotLogo} className="App-logo" alt="Chatbot Logo" />
            <form onSubmit={handleSubmit} className="App-form">
              <h2>Inicio de Sesión</h2>
              <label>
                Correo:
                <input type="email" value={email} onChange={handleEmailChange} required />
              </label>
              <label>
                Contraseña:
                <input type="password" value={password} onChange={handlePasswordChange} required />
              </label>
              <button type="submit">Iniciar Sesión</button>
              {error && <p className="error">{error}</p>}
            </form>
          </header>
          <footer className="App-footer">
            <img src={OracleLogo} className="Footer-logo" alt="Oracle Logo" />
          </footer>
        </Route>
        <Route path="/success">
          <div className="success-message">
            <h2>Inicio de sesión exitoso</h2>
          </div>
        </Route>
      </div>
    </Router>
  );
}

export default App;
