import React, { useState } from 'react';
import './App.css';
import ChatbotLogo from './Images/ChatBotLogo.png';
import OracleLogo from './Images/OracleLogo.png';
import { signInWithEmailAndPassword } from 'firebase/auth';
import { auth, firestore } from './Database Connection/firebase'; // Importa auth y firestore
import { addDoc, collection, doc, getDoc } from 'firebase/firestore'; // Importa addDoc y collection desde Firestore
import HomeScreenManager from './Screens/homeScreenManager';
import HomeScreenUser from './Screens/homeScreenUser.js';

function App() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loggedIn, setLoggedIn] = useState(false);
  const [isManager, setIsManager] = useState(false); // Nuevo estado para almacenar si el usuario es gerente

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const userCredential = await signInWithEmailAndPassword(auth, email, password);
      const user = userCredential.user;

      // Obtener el documento de usuario recién creado desde Firestore
      const userDoc = await getUserDocument(user.uid);

      // Obtener el valor de isManager del documento de usuario
      const userData = userDoc.data();
      const managerStatus = userData.isManager;

      // Actualizar el estado de isManager
      setIsManager(managerStatus);

      setLoggedIn(true);
    } catch (error) {
      setError('Correo o contraseña incorrectos');
      console.error('Error al iniciar sesión:', error.message);
    }
  };

  const getUserDocument = async (uid) => {
    try {
      // Obtener el documento de usuario de Firestore
      const docRef = doc(firestore, 'usuarios', uid);
      const docSnap = await getDoc(docRef);

      return docSnap;
    } catch (error) {
      console.error('Error al obtener documento de usuario en Firestore:', error.message);
    }
  };

  if (loggedIn) {
    // Redirigir al usuario a la página correspondiente según isManager
    return isManager ? <HomeScreenManager /> : <HomeScreenUser />;
  }

  // Renderizar el formulario de inicio de sesión si el usuario no está autenticado
  return (
    <div className="App">
      <header className="App-header">
        <img src={ChatbotLogo} className="App-logo" alt="Chatbot Logo" />
        <form onSubmit={handleSubmit} className="App-form">
          <h2>Inicio de Sesión</h2>
          {error && <p style={{ color: 'red' }}>{error}</p>}
          <label>
            Correo:
            <input type="email" value={email} onChange={handleEmailChange} required />
          </label>
          <label>
            Contraseña:
            <input type="password" value={password} onChange={handlePasswordChange} required />
          </label>
          <button type="submit">Iniciar Sesión</button>
        </form>
        <img src={OracleLogo} className="Oracle-logo" alt="Oracle Logo" />
      </header>
    </div>
  );
}

export default App;
