import React, { useState } from 'react';
import './App.css';
import ChatbotLogo from './Images/ChatBotLogo.png';
import OracleLogo from './Images/OracleLogo.png';
import { signInWithEmailAndPassword } from 'firebase/auth';
import { auth, firestore } from './Database Connection/firebase'; // Importa auth y firestore
import { addDoc, collection, getDocs, query, where } from 'firebase/firestore'; // Importa las funciones necesarias desde Firestore
import HomeScreen from './Screens/homeScreen';
import HomeScreenManager from './Screens/homeScreenManager';

function App() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loggedIn, setLoggedIn] = useState(false);
  const [isManager, setIsManager] = useState(false);

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const userCredential = await signInWithEmailAndPassword(auth, email, password);
      const user = userCredential.user;

      // Verificar si el usuario ya está registrado
      const userExists = await checkUserExists(user.email);

      if (!userExists) {
        // Crear un nuevo documento de usuario en Firestore
        await createUserDocument(user.email);
      }

      // Verificar si el usuario es un manager
      await checkIsManager(user.uid);

      setLoggedIn(true);
    } catch (error) {
      setError('Correo o contraseña incorrectos');
      console.error('Error al iniciar sesión:', error.message);
    }
  };

  const checkUserExists = async (email) => {
    try {
      // Consultar la colección de usuarios en Firestore para verificar si el usuario ya está registrado
      const q = query(collection(firestore, 'usuarios'), where('correo', '==', email));
      const querySnapshot = await getDocs(q);
      return !querySnapshot.empty;
    } catch (error) {
      console.error('Error al verificar si el usuario ya está registrado:', error.message);
      return false;
    }
  };

  const createUserDocument = async (email) => {
    try {
      // Añadir un documento de usuario con el correo electrónico del usuario a la colección "usuarios" en Firestore
      await addDoc(collection(firestore, 'usuarios'), {
        correo: email
      });
    } catch (error) {
      console.error('Error al crear documento de usuario en Firestore:', error.message);
    }
  };

  const checkIsManager = async (email) => {
    try {
      // Consultar la colección de usuarios en Firestore para buscar el documento del usuario por su correo electrónico
      const q = query(collection(firestore, 'usuarios'), where('correo', '==', email));
      const querySnapshot = await getDocs(q);

      // Verificar si se encontró el documento del usuario
      if (!querySnapshot.empty) {
        // Obtener el primer documento encontrado (suponiendo que no haya duplicados de correo electrónico)
        const docSnapshot = querySnapshot.docs[0];
        const userData = docSnapshot.data();

        // Verificar si el usuario es un gerente
        if (userData.isManager === true) {
          setIsManager(true);
        }
      }
    } catch (error) {
      console.error('Error al verificar si el usuario es un gerente:', error.message);
    }
  };  

  if (loggedIn) {
    if (isManager) {
      return <HomeScreenManager />;
    } else {
      return <HomeScreen />;
    }
  }

  return (
    <div className="App">
      <header className="App-header">
        <img src={ChatbotLogo} className="App-logo" alt="Chatbot Logo" />
        <form onSubmit={handleSubmit} className="App-form">
          <h2>Inicio de Sesión</h2>
          {error && <p style={{ color: 'red' }}>{error}</p>}
          <label>
            Correo:
            <input type="email" value={email} onChange={handleEmailChange} required />
          </label>
          <label>
            Contraseña:
            <input type="password" value={password} onChange={handlePasswordChange} required />
          </label>
          <button type="submit">Iniciar Sesión</button>
        </form>
        <img src={OracleLogo} className="Oracle-logo" alt="Oracle Logo" />
      </header>
    </div>
  );
}

export default App;
