import React, { useState } from 'react';
import './App.css';
import ChatbotLogo from './Images/ChatBotLogo.png';
import OracleLogo from './Images/OracleLogo.png';
import { signInWithEmailAndPassword } from 'firebase/auth';
import { auth, firestore } from './Database Connection/firebase';
import HomeScreen from './Screens/homeScreen';
import { query, where, getDocs, collection, addDoc } from 'firebase/firestore';

function App() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loggedIn, setLoggedIn] = useState(false);

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Verificar si el usuario ya existe en Firestore
      const userExists = await checkUserExists(email);

      if (userExists) {
        // El usuario ya existe, redirigir a la pantalla de inicio
        setLoggedIn(true);
      } else {
        // El usuario no existe, iniciar sesión y crear un nuevo documento de usuario
        const userCredential = await signInWithEmailAndPassword(auth, email, password);
        const user = userCredential.user;
        await createUserDocument(user.email);
        setLoggedIn(true);
      }
    } catch (error) {
      setError('Correo o contraseña incorrectos');
      console.error('Error al iniciar sesión:', error.message);
    }
  };

  const checkUserExists = async (email) => {
    // Realizar una consulta a Firestore para buscar un usuario con el correo electrónico proporcionado
    const userQuery = query(collection(firestore, 'usuarios'), where('correo', '==', email));
    const querySnapshot = await getDocs(userQuery);

    return !querySnapshot.empty; // Devuelve true si hay algún documento con el correo electrónico dado
  };

  const createUserDocument = async (email) => {
    try {
      // Crear un nuevo documento de usuario en Firestore
      await addDoc(collection(firestore, 'usuarios'), {
        correo: email
      });
    } catch (error) {
      console.error('Error al crear documento de usuario en Firestore:', error.message);
    }
  };

  if (loggedIn) {
    return <HomeScreen />;
  }

  return (
    <div className="App">
      <header className="App-header">
        <img src={ChatbotLogo} className="App-logo" alt="Chatbot Logo" />
        <form onSubmit={handleSubmit} className="App-form">
          <h2>Inicio de Sesión</h2>
          {error && <p style={{ color: 'red' }}>{error}</p>}
          <label>
            Correo:
            <input type="email" value={email} onChange={handleEmailChange} required />
          </label>
          <label>
            Contraseña:
            <input type="password" value={password} onChange={handlePasswordChange} required />
          </label>
          <button type="submit">Iniciar Sesión</button>
        </form>
        <img src={OracleLogo} className="Oracle-logo" alt="Oracle Logo" />
      </header>
    </div>
  );
}

export default App;
