import React, { useState } from 'react';
import './App.css';
import ChatbotLogo from './Images/ChatBotLogo.png';
import OracleLogo from './Images/OracleLogo.png';
import { signInWithEmailAndPassword } from 'firebase/auth';
import { auth } from './Database Connection/firebase';
import { getFirestore, collection, doc, setDoc } from 'firebase/firestore';
import HomeScreen from './Screens/homeScreen';

function App() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loggedIn, setLoggedIn] = useState(false);

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Iniciar sesión con Firebase Authentication
      await signInWithEmailAndPassword(auth, email, password);

      // Una vez iniciada la sesión, crear una cuenta en Firestore
      const db = getFirestore();
      const userRef = doc(db, 'users', auth.currentUser.uid); // Suponiendo que tienes una colección 'users' en Firestore
      await setDoc(userRef, {
        email: auth.currentUser.email,
        name: '', // Nombre vacío por defecto
        isManager: false // isManager en false por defecto
      });

      // Establecer el estado de loggedIn a true
      setLoggedIn(true);
    } catch (error) {
      setError('Correo o contraseña incorrectos');
      console.error('Error al iniciar sesión:', error.message);
    }
  };

  if (loggedIn) {
    return <HomeScreen />;
  }

  return (
    <div className="App">
      <header className="App-header">
        <img src={ChatbotLogo} className="App-logo" alt="Chatbot Logo" />
        <form onSubmit={handleSubmit} className="App-form">
          <h2>Inicio de Sesión</h2>
          {error && <p style={{ color: 'red' }}>{error}</p>}
          <label>
            Correo:
            <input type="email" value={email} onChange={handleEmailChange} required />
          </label>
          <label>
            Contraseña:
            <input type="password" value={password} onChange={handlePasswordChange} required />
          </label>
          <button type="submit">Iniciar Sesión</button>
        </form>
        <img src={OracleLogo} className="Oracle-logo" alt="Oracle Logo" />
      </header>
    </div>
  );
}

export default App;
